mapper.select:
SELECT B.user_id AS userId, username, author, email, role, user_status As userStatus, E.points,
  E.signature, B.update_date As updateDate, B.create_date As createDate
  FROM user_basic B LEFT JOIN user_extend E ON B.user_id = E.user_id WHERE username = #{username}

SELECT B.user_id AS userId, username, author, email, role, user_status As userStatus, E.points,
  E.signature, B.update_date As updateDate, B.create_date As createDate
  FROM user_basic B LEFT JOIN user_extend E ON B.user_id = E.user_id WHERE B.user_id = #{userId}
